import Config from '../config.json';
import Errors from './errors';

import ObjectValidator from './objectvalidator';

class FCMongoDBHandler{
    constructor(config){
        this.config_provided = config;
        this.config = {
            host: null,
            port: null,
            database: null,
            username: null,
            password: null,
            table_name: null,
        };

        this.init();
    }

    async init(){
