const { DataTypes, Model, Op } = require('sequelize');
const { sequelize } = require("../mysqlhandler");

class File extends Model{

}

function newFile(config){
    const sequelize = config.s;
    const tableName = config.t;

    return File.init({
        id: {
            type: DataTypes.BIGINT(11),
            primaryKey: true,
            autoIncrement: true,
            allowNull: false,
        },

        name: {
            type: DataTypes.STRING,
            allowNull: false,
        },

        folder: {
            type: DataTypes.TEXT,
            allowNull: false,
            defaultValue: "/",
        },

        path: {
            type: DataTypes.TEXT,
            allowNull: false,
        },

        extension: {
            type: DataTypes.STRING,
            allowNull: false,
        },

        size: {
            type: DataTypes.BIGINT,
            allowNull: false,
            defaultValue: 0,
        },

        isDeleted: {
            type: DataTypes.BOOLEAN,
            allowNull: false,
            defaultValue: 0,
        },

        deleted_at: {
            type: DataTypes.DATE,
            allowNull: true,
        },
