                }else{
                    // If file size is greater than 5MB, read with stream.
                    try{
                        const readStream = fs.createReadStream(path);
                        readStream.pause();

                        if(doDecrypt === true && self.file_protector !== null){
                            let decrypt = new stream.Transform({ decodeStrings: false });
                            decrypt._transform = async function(chunk, encoding, done) {
                                let decrypted = await self.file_protector.read(chunk);
                                done(null, decrypted);
                            };

                            const pipeline = pipelineWithoutPromise(readStream, decrypt, (error) => { console.log("ERROR: ", error); });
                            readStream.pause();

                            return { isStream: true, contents: pipeline, readStream: readStream, contentType: contentType, };
                        }else{
                            return { isStream: true, contents: readStream, readStream: readStream, contentType: contentType, };
                        }
                    }catch(error){ return { isStream: false, contents: null, readStream: null, contentType: null, }; }
                }
            }
        }

        return { isStream: false, contents: null, readStream: null, contentType: null, };
